% s1 = 0.95;
% s2 = 1;
if s2 ~= 0
    ratio = s1/s2;
    if ratio > 1 || ratio < -1
        ratio = s2/s1;
    end
elseif s2 == 0 && s1 ~= 0
    ratio = s2/s1;
elseif s2 == 0 && s1 == 0
    ratio = 0;
end

indices = find(table(1,:) == ratio);
angle_output = [];

if ~isempty(indices)
    % find if there is any match
    angle_output = [angle_output, table(2, indices)];
else
    for i = 1:length(table)-1
        if (table(1,i)<ratio && table(1,i+1)>ratio) ||...
                (table(1,i)>ratio && table(1,i+1)<ratio)
            x = [table(1, i), table(1, i+1)];
            v = [table(2, i), table(2, i+1)];    
            angle_output = [angle_output, interp1(x, v, ratio, 'linear')];
        end
    end
end

% find right angle according to s1,s2 as well as abs(s1)-abs(s2)
if length(angle_output) ~= 1
    % first quadrant and below 45 degree
    if (s1 > 0 && s2 > 0) && (abs(s1)-abs(s2))<=0
        % first quadrant and below 45 degree
        angle_output = angle_output(angle_output <= 45 & angle_output > 0);
    elseif (s1 > 0 && s2 > 0) && (abs(s1)-abs(s2))>0
        angle_output = angle_output(angle_output <= 90 & angle_output > 45);
    elseif (s1 > 0 && s2 < 0) && (abs(s1)-abs(s2))>=0
        angle_output = angle_output(angle_output <= 135 & angle_output > 90);
    elseif (s1 > 0 && s2 < 0) && (abs(s1)-abs(s2))<0
        angle_output = angle_output(angle_output <= 180 & angle_output > 135);
    elseif (s1 < 0 && s2 < 0) && (abs(s1)-abs(s2))<=0
        angle_output = angle_output(angle_output <= 225 & angle_output > 180) - 360;
    elseif (s1 < 0 && s2 < 0) && (abs(s1)-abs(s2))>0
        angle_output = angle_output(angle_output <= 270 & angle_output > 225) - 360;
    elseif (s1 < 0 && s2 > 0) && (abs(s1)-abs(s2))>=0
        angle_output = angle_output(angle_output <= 315 & angle_output > 270) - 360;
    else
        angle_output = angle_output(angle_output <= 360 & angle_output > 315) - 360;
    end
end

% find right angle when angle is 0, 90, 180, -90, -180
if any(angle_output == 0) || any(angle_output == 180) || any(angle_output == -180) || any(angle_output == 90) || any(angle_output == -90)
    % first quadrant and below 45 degree
    if s1==0 && s2>=0
        angle_output = 0;
    elseif s2==0 && s1>0
        angle_output = 90;
    elseif s1==0 && s2<0
        angle_output = 180;
    elseif s2==0 && s1<0
        angle_output = -90;
    end
end

